contracts/invoice/InvoiceEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/// @title InvoiceEscrow
/// @notice Secure escrow contract for individual invoice payments
/// @dev Deployed per invoice for enhanced security and tracking
contract InvoiceEscrow is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ═══════════════════════════════════════════════════════════════════════
    // STATE VARIABLES
    // ═══════════════════════════════════════════════════════════════════════

    /// Invoice details
    string public invoiceId;
    string public invoiceNumber;
    address public seller;
    address public buyer;
    uint256 public totalAmount;
    uint256 public paidAmount;
    uint256 public dueDate;
    bool public isCompleted;
    bool public isCancelled;
    bool public isDisputed;

    /// Payment token
    address public paymentToken;
    uint8 public tokenDecimals;

    /// Payment splits (for multi-recipient invoices)
    struct PaymentSplit {
        address recipient;
        uint256 percentage; // In basis points (10000 = 100%)
        uint256 fixedAmount; // If 0, use percentage
        bool isPrimary;
    }
    PaymentSplit[] public paymentSplits;
    uint256 public totalSplitPercentage;

    /// Dispute resolution
    address public arbiter;
    uint256 public disputeRaisedAt;
    string public disputeReason;

    /// Events
    event PaymentReceived(address indexed payer, uint256 amount, uint256 timestamp);
    event PaymentDistributed(address indexed recipient, uint256 amount);
    event InvoiceCompleted(uint256 timestamp);
    event InvoiceCancelled(uint256 timestamp);
    event DisputeRaised(address indexed raiser, string reason, uint256 timestamp);
    event DisputeResolved(bool sellerFavored, uint256 timestamp);
    event PaymentSplitAdded(address indexed recipient, uint256 percentage, uint256 fixedAmount);

    // ═══════════════════════════════════════════════════════════════════════
    // CONSTRUCTOR
    // ═══════════════════════════════════════════════════════════════════════

    constructor(
        string memory _invoiceId,
        string memory _invoiceNumber,
        address _seller,
        address _buyer,
        uint256 _totalAmount,
        uint256 _dueDate,
        address _paymentToken,
        address _arbiter
    ) Ownable(msg.sender) {
        require(_seller != address(0), "Invalid seller");
        require(_buyer != address(0), "Invalid buyer");
        require(_totalAmount > 0, "Amount must be > 0");
        require(_dueDate > block.timestamp, "Due date must be in future");

        invoiceId = _invoiceId;
        invoiceNumber = _invoiceNumber;
        seller = _seller;
        buyer = _buyer;
        totalAmount = _totalAmount;
        dueDate = _dueDate;
        paymentToken = _paymentToken;
        arbiter = _arbiter;

        // Get token decimals
        if (_paymentToken != address(0)) {
            tokenDecimals = IERC20Metadata(_paymentToken).decimals();
        } else {
            tokenDecimals = 18; // Native token (ETH, MATIC, etc.)
        }
    }

    // ═══════════════════════════════════════════════════════════════════════
    // PAYMENT FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Pay invoice (full or partial)
    /// @param amount Amount to pay
    function pay(uint256 amount) external payable nonReentrant {
        require(!isCompleted, "Invoice already completed");
        require(!isCancelled, "Invoice cancelled");
        require(amount > 0, "Amount must be > 0");
        require(paidAmount + amount <= totalAmount, "Exceeds total amount");

        if (paymentToken == address(0)) {
            // Native token payment
            require(msg.value == amount, "Incorrect ETH amount");
        } else {
            // ERC20 payment
            require(msg.value == 0, "Do not send ETH for token payment");
            IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        paidAmount += amount;

        emit PaymentReceived(msg.sender, amount, block.timestamp);

        // Auto-complete if fully paid
        if (paidAmount == totalAmount) {
            _completeInvoice();
        }
    }

    /// @notice Pay full invoice amount
    function payFull() external payable nonReentrant {
        uint256 remaining = totalAmount - paidAmount;
        require(remaining > 0, "Already fully paid");

        if (paymentToken == address(0)) {
            require(msg.value == remaining, "Incorrect ETH amount");
        } else {
            require(msg.value == 0, "Do not send ETH for token payment");
            IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), remaining);
        }

        paidAmount = totalAmount;

        emit PaymentReceived(msg.sender, remaining, block.timestamp);

        _completeInvoice();
    }

    // ═══════════════════════════════════════════════════════════════════════
    // PAYMENT SPLITS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Add payment split (only owner/factory)
    function addPaymentSplit(
        address recipient,
        uint256 percentage,
        uint256 fixedAmount,
        bool isPrimary
    ) external onlyOwner {
        require(recipient != address(0), "Invalid recipient");
        require(paymentSplits.length == 0 || !isCompleted, "Cannot modify after completion");

        if (fixedAmount == 0) {
            require(percentage > 0 && percentage <= 10000, "Invalid percentage");
            require(totalSplitPercentage + percentage <= 10000, "Total exceeds 100%");
            totalSplitPercentage += percentage;
        }

        paymentSplits.push(PaymentSplit({
            recipient: recipient,
            percentage: percentage,
            fixedAmount: fixedAmount,
            isPrimary: isPrimary
        }));

        emit PaymentSplitAdded(recipient, percentage, fixedAmount);
    }

    /// @notice Distribute payments to split recipients (public for manual distribution)
    function distributePayments() public nonReentrant {
        require(isCompleted, "Invoice not completed");
        require(paymentSplits.length > 0, "No payment splits");

        uint256 remaining = paidAmount;

        for (uint256 i = 0; i < paymentSplits.length; i++) {
            PaymentSplit memory split = paymentSplits[i];
            uint256 amount;

            if (split.fixedAmount > 0) {
                amount = split.fixedAmount;
            } else {
                amount = (paidAmount * split.percentage) / 10000;
            }

            if (amount > remaining) {
                amount = remaining;
            }

            if (amount > 0) {
                _transferFunds(split.recipient, amount);
                remaining -= amount;
                emit PaymentDistributed(split.recipient, amount);
            }
        }

        // Send any remaining to primary recipient or seller
        if (remaining > 0) {
            address finalRecipient = seller;
            for (uint256 i = 0; i < paymentSplits.length; i++) {
                if (paymentSplits[i].isPrimary) {
                    finalRecipient = paymentSplits[i].recipient;
                    break;
                }
            }
            _transferFunds(finalRecipient, remaining);
            emit PaymentDistributed(finalRecipient, remaining);
        }
    }

    // ═══════════════════════════════════════════════════════════════════════
    // RELEASE FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Release funds to seller (only owner after due date or manual release)
    function releaseFunds() external nonReentrant {
        require(isCompleted, "Invoice not completed");
        require(!isDisputed, "Invoice is disputed");
        require(
            msg.sender == owner() || msg.sender == seller || block.timestamp > dueDate + 30 days,
            "Not authorized or too early"
        );

        if (paymentSplits.length > 0) {
            // Use split distribution
            distributePayments();
        } else {
            // Simple release to seller
            _transferFunds(seller, paidAmount);
            emit PaymentDistributed(seller, paidAmount);
        }
    }

    /// @notice Auto-release after grace period
    function autoRelease() external nonReentrant {
        require(isCompleted, "Invoice not completed");
        require(!isDisputed, "Invoice is disputed");
        require(block.timestamp > dueDate + 30 days, "Grace period not elapsed");

        if (paymentSplits.length > 0) {
            distributePayments();
        } else {
            _transferFunds(seller, paidAmount);
            emit PaymentDistributed(seller, paidAmount);
        }
    }

    // ═══════════════════════════════════════════════════════════════════════
    // DISPUTE RESOLUTION
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Raise dispute
    function raiseDispute(string calldata reason) external {
        require(!isCompleted || paidAmount > 0, "Nothing to dispute");
        require(!isCancelled, "Invoice cancelled");
        require(!isDisputed, "Already disputed");
        require(
            msg.sender == seller || msg.sender == buyer,
            "Only parties can dispute"
        );

        isDisputed = true;
        disputeRaisedAt = block.timestamp;
        disputeReason = reason;

        emit DisputeRaised(msg.sender, reason, block.timestamp);
    }

    /// @notice Resolve dispute (only arbiter)
    function resolveDispute(bool sellerFavored) external {
        require(msg.sender == arbiter || msg.sender == owner(), "Only arbiter");
        require(isDisputed, "No active dispute");

        isDisputed = false;

        if (sellerFavored) {
            // Release funds to seller
            if (paymentSplits.length > 0) {
                distributePayments();
            } else {
                _transferFunds(seller, paidAmount);
                emit PaymentDistributed(seller, paidAmount);
            }
        } else {
            // Refund to buyer
            _transferFunds(buyer, paidAmount);
            emit PaymentDistributed(buyer, paidAmount);
        }

        emit DisputeResolved(sellerFavored, block.timestamp);
    }

    // ═══════════════════════════════════════════════════════════════════════
    // ADMIN FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Cancel invoice (only seller before any payment)
    function cancel() external {
        require(msg.sender == seller || msg.sender == owner(), "Not authorized");
        require(paidAmount == 0, "Cannot cancel after payment");
        require(!isCancelled, "Already cancelled");

        isCancelled = true;

        emit InvoiceCancelled(block.timestamp);
    }

    /// @notice Update arbiter
    function updateArbiter(address newArbiter) external onlyOwner {
        require(newArbiter != address(0), "Invalid arbiter");
        arbiter = newArbiter;
    }

    // ═══════════════════════════════════════════════════════════════════════
    // INTERNAL FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    function _completeInvoice() internal {
        isCompleted = true;
        emit InvoiceCompleted(block.timestamp);
    }

    function _transferFunds(address to, uint256 amount) internal {
        if (paymentToken == address(0)) {
            // Native token
            (bool success, ) = to.call{value: amount}("");
            require(success, "ETH transfer failed");
        } else {
            // ERC20
            IERC20(paymentToken).safeTransfer(to, amount);
        }
    }

    // ═══════════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    function getRemainingAmount() external view returns (uint256) {
        return totalAmount - paidAmount;
    }

    function getPaymentProgress() external view returns (uint256) {
        if (totalAmount == 0) return 0;
        return (paidAmount * 10000) / totalAmount; // Basis points
    }

    function isOverdue() external view returns (bool) {
        return block.timestamp > dueDate && !isCompleted;
    }

    function getDaysOverdue() external view returns (uint256) {
        if (block.timestamp <= dueDate || isCompleted) return 0;
        return (block.timestamp - dueDate) / 1 days;
    }

    function getPaymentSplitsCount() external view returns (uint256) {
        return paymentSplits.length;
    }

    function getPaymentSplit(uint256 index) external view returns (
        address recipient,
        uint256 percentage,
        uint256 fixedAmount,
        bool isPrimary
    ) {
        require(index < paymentSplits.length, "Invalid index");
        PaymentSplit memory split = paymentSplits[index];
        return (split.recipient, split.percentage, split.fixedAmount, split.isPrimary);
    }

    // ═══════════════════════════════════════════════════════════════════════
    // FALLBACK
    // ═══════════════════════════════════════════════════════════════════════

    receive() external payable {
        revert("Use pay() function");
    }
}

contracts/invoice/InvoiceFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./InvoiceEscrow.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/// @title InvoiceFactory
/// @notice Factory contract for deploying individual invoice escrow contracts
/// @dev Creates and manages invoice escrow contracts
contract InvoiceFactory is Ownable, ReentrancyGuard {
    // ═══════════════════════════════════════════════════════════════════════
    // STATE VARIABLES
    // ═══════════════════════════════════════════════════════════════════════

    /// Mapping of invoice ID to escrow contract
    mapping(string => address) public invoiceEscrows;

    /// Array of all escrow addresses
    address[] public allEscrows;

    /// Mapping of user to their invoices (as seller)
    mapping(address => address[]) public sellerInvoices;

    /// Mapping of user to their invoices (as buyer)
    mapping(address => address[]) public buyerInvoices;

    /// Default arbiter
    address public defaultArbiter;

    /// Platform fee (in basis points, 100 = 1%)
    uint256 public platformFee;
    address public feeCollector;

    /// Events
    event InvoiceEscrowCreated(
        string indexed invoiceId,
        address indexed escrowAddress,
        address indexed seller,
        address buyer,
        uint256 amount
    );
    event PlatformFeeUpdated(uint256 newFee);
    event FeeCollectorUpdated(address newCollector);
    event DefaultArbiterUpdated(address newArbiter);

    // ═══════════════════════════════════════════════════════════════════════
    // CONSTRUCTOR
    // ═══════════════════════════════════════════════════════════════════════

    constructor(address _defaultArbiter, address _feeCollector) Ownable(msg.sender) {
        require(_defaultArbiter != address(0), "Invalid arbiter");
        require(_feeCollector != address(0), "Invalid fee collector");

        defaultArbiter = _defaultArbiter;
        feeCollector = _feeCollector;
        platformFee = 50; // 0.5% default
    }

    // ═══════════════════════════════════════════════════════════════════════
    // ESCROW CREATION
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Create new invoice escrow
    function createInvoiceEscrow(
        string calldata invoiceId,
        string calldata invoiceNumber,
        address seller,
        address buyer,
        uint256 totalAmount,
        uint256 dueDate,
        address paymentToken,
        address customArbiter
    ) external nonReentrant returns (address) {
        address arbiter = customArbiter != address(0) ? customArbiter : defaultArbiter;

        return _createEscrowInternal(
            invoiceId,
            invoiceNumber,
            seller,
            buyer,
            totalAmount,
            dueDate,
            paymentToken,
            arbiter
        );
    }

    /// @notice Create invoice with payment splits
    function createInvoiceWithSplits(
        string calldata invoiceId,
        string calldata invoiceNumber,
        address seller,
        address buyer,
        uint256 totalAmount,
        uint256 dueDate,
        address paymentToken,
        address customArbiter,
        address[] calldata splitRecipients,
        uint256[] calldata splitPercentages,
        uint256[] calldata splitFixedAmounts,
        bool[] calldata splitIsPrimary
    ) external nonReentrant returns (address) {
        require(splitRecipients.length == splitPercentages.length, "Length mismatch");
        require(splitRecipients.length == splitFixedAmounts.length, "Length mismatch");
        require(splitRecipients.length == splitIsPrimary.length, "Length mismatch");

        // Create escrow
        address escrowAddress = this.createInvoiceEscrow(
            invoiceId,
            invoiceNumber,
            seller,
            buyer,
            totalAmount,
            dueDate,
            paymentToken,
            customArbiter
        );

        // Add payment splits
        InvoiceEscrow escrow = InvoiceEscrow(payable(escrowAddress));
        for (uint256 i = 0; i < splitRecipients.length; i++) {
            escrow.addPaymentSplit(
                splitRecipients[i],
                splitPercentages[i],
                splitFixedAmounts[i],
                splitIsPrimary[i]
            );
        }

        return escrowAddress;
    }

    // ═══════════════════════════════════════════════════════════════════════
    // BATCH OPERATIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Create multiple invoices in one transaction
    function batchCreateInvoices(
        string[] calldata invoiceIds,
        string[] calldata invoiceNumbers,
        address[] calldata sellers,
        address[] calldata buyers,
        uint256[] calldata amounts,
        uint256[] calldata dueDates,
        address[] calldata paymentTokens
    ) external nonReentrant returns (address[] memory escrows) {
        uint256 length = invoiceIds.length;
        require(length == sellers.length, "Length mismatch");
        require(length == buyers.length, "Length mismatch");
        require(length == amounts.length, "Length mismatch");
        require(length == dueDates.length, "Length mismatch");

        escrows = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            escrows[i] = _createEscrowInternal(
                invoiceIds[i],
                invoiceNumbers[i],
                sellers[i],
                buyers[i],
                amounts[i],
                dueDates[i],
                paymentTokens[i],
                defaultArbiter
            );
        }
    }

    /// @notice Internal escrow creation to avoid stack too deep
    function _createEscrowInternal(
        string calldata invoiceId,
        string calldata invoiceNumber,
        address seller,
        address buyer,
        uint256 totalAmount,
        uint256 dueDate,
        address paymentToken,
        address arbiter
    ) internal returns (address) {
        require(bytes(invoiceId).length > 0, "Invalid invoice ID");
        require(invoiceEscrows[invoiceId] == address(0), "Invoice already exists");
        require(seller != address(0), "Invalid seller");
        require(buyer != address(0), "Invalid buyer");
        require(totalAmount > 0, "Amount must be > 0");

        // Deploy new escrow
        InvoiceEscrow escrow = new InvoiceEscrow(
            invoiceId,
            invoiceNumber,
            seller,
            buyer,
            totalAmount,
            dueDate,
            paymentToken,
            arbiter
        );

        address escrowAddress = address(escrow);

        // Store escrow
        invoiceEscrows[invoiceId] = escrowAddress;
        allEscrows.push(escrowAddress);
        sellerInvoices[seller].push(escrowAddress);
        buyerInvoices[buyer].push(escrowAddress);

        emit InvoiceEscrowCreated(
            invoiceId,
            escrowAddress,
            seller,
            buyer,
            totalAmount
        );

        return escrowAddress;
    }

    // ═══════════════════════════════════════════════════════════════════════
    // ADMIN FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Update platform fee
    function updatePlatformFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "Fee too high (max 10%)");
        platformFee = newFee;
        emit PlatformFeeUpdated(newFee);
    }

    /// @notice Update fee collector
    function updateFeeCollector(address newCollector) external onlyOwner {
        require(newCollector != address(0), "Invalid collector");
        feeCollector = newCollector;
        emit FeeCollectorUpdated(newCollector);
    }

    /// @notice Update default arbiter
    function updateDefaultArbiter(address newArbiter) external onlyOwner {
        require(newArbiter != address(0), "Invalid arbiter");
        defaultArbiter = newArbiter;
        emit DefaultArbiterUpdated(newArbiter);
    }

    /// @notice Emergency pause (for specific escrow)
    function emergencyPauseEscrow(string calldata invoiceId) external onlyOwner {
        address escrowAddress = invoiceEscrows[invoiceId];
        require(escrowAddress != address(0), "Escrow not found");
        // Implementation would require Pausable in InvoiceEscrow
    }

    // ═══════════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Get escrow address by invoice ID
    function getEscrowByInvoiceId(string calldata invoiceId) external view returns (address) {
        return invoiceEscrows[invoiceId];
    }

    /// @notice Get all escrows for a seller
    function getSellerInvoices(address seller) external view returns (address[] memory) {
        return sellerInvoices[seller];
    }

    /// @notice Get all escrows for a buyer
    function getBuyerInvoices(address buyer) external view returns (address[] memory) {
        return buyerInvoices[buyer];
    }

    /// @notice Get total number of escrows
    function getTotalEscrows() external view returns (uint256) {
        return allEscrows.length;
    }

    /// @notice Get escrow at index
    function getEscrowAtIndex(uint256 index) external view returns (address) {
        require(index < allEscrows.length, "Index out of bounds");
        return allEscrows[index];
    }

    /// @notice Get invoice details from escrow
    function getInvoiceDetails(string calldata invoiceId) external view returns (
        address escrowAddress,
        address seller,
        address buyer,
        uint256 totalAmount,
        uint256 paidAmount,
        uint256 dueDate,
        bool isCompleted,
        bool isCancelled,
        bool isDisputed
    ) {
        escrowAddress = invoiceEscrows[invoiceId];
        require(escrowAddress != address(0), "Escrow not found");

        InvoiceEscrow escrow = InvoiceEscrow(payable(escrowAddress));

        return (
            escrowAddress,
            escrow.seller(),
            escrow.buyer(),
            escrow.totalAmount(),
            escrow.paidAmount(),
            escrow.dueDate(),
            escrow.isCompleted(),
            escrow.isCancelled(),
            escrow.isDisputed()
        );
    }

    /// @notice Check if invoice escrow exists
    function invoiceExists(string calldata invoiceId) external view returns (bool) {
        return invoiceEscrows[invoiceId] != address(0);
    }

    /// @notice Get seller invoice count
    function getSellerInvoiceCount(address seller) external view returns (uint256) {
        return sellerInvoices[seller].length;
    }

    /// @notice Get buyer invoice count
    function getBuyerInvoiceCount(address buyer) external view returns (uint256) {
        return buyerInvoices[buyer].length;
    }

    /// @notice Get platform statistics
    function getPlatformStats() external view returns (
        uint256 totalInvoices,
        uint256 totalVolume,
        uint256 completedInvoices,
        uint256 disputedInvoices
    ) {
        totalInvoices = allEscrows.length;
        uint256 volume = 0;
        uint256 completed = 0;
        uint256 disputed = 0;

        for (uint256 i = 0; i < allEscrows.length; i++) {
            InvoiceEscrow escrow = InvoiceEscrow(payable(allEscrows[i]));
            volume += escrow.totalAmount();
            if (escrow.isCompleted()) completed++;
            if (escrow.isDisputed()) disputed++;
        }

        return (totalInvoices, volume, completed, disputed);
    }
}

contracts/invoice/InvoiceRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/// @title InvoiceRegistry
/// @notice On-chain registry for invoice tracking and verification
/// @dev Stores invoice metadata and IPFS/Arweave references
contract InvoiceRegistry is AccessControl, ReentrancyGuard {
    // ═══════════════════════════════════════════════════════════════════════
    // ROLES
    // ═══════════════════════════════════════════════════════════════════════

    bytes32 public constant REGISTRAR_ROLE = keccak256("REGISTRAR_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    // ═══════════════════════════════════════════════════════════════════════
    // STRUCTS
    // ═══════════════════════════════════════════════════════════════════════

    enum InvoiceStatus {
        Draft,
        Sent,
        Viewed,
        Pending,
        PartiallyPaid,
        Paid,
        Overdue,
        Cancelled,
        Disputed,
        Refunded
    }

    struct InvoiceMetadata {
        string invoiceId;
        string invoiceNumber;
        address seller;
        address buyer;
        uint256 totalAmount;
        uint256 paidAmount;
        uint256 issueDate;
        uint256 dueDate;
        InvoiceStatus status;
        string ipfsCid;
        string arweaveTxId;
        address escrowAddress;
        bool isRecurring;
        bool isFactored;
        bool exists;
    }

    struct InvoicePayment {
        address payer;
        uint256 amount;
        uint256 timestamp;
        string txHash;
        uint256 chainId;
    }

    // ═══════════════════════════════════════════════════════════════════════
    // STATE VARIABLES
    // ═══════════════════════════════════════════════════════════════════════

    /// Mapping of invoice ID to metadata
    mapping(string => InvoiceMetadata) public invoices;

    /// Mapping of invoice ID to payments
    mapping(string => InvoicePayment[]) public invoicePayments;

    /// Mapping of seller to invoice IDs
    mapping(address => string[]) public sellerInvoices;

    /// Mapping of buyer to invoice IDs
    mapping(address => string[]) public buyerInvoices;

    /// Array of all invoice IDs
    string[] public allInvoiceIds;

    /// Mapping for invoice number uniqueness
    mapping(string => bool) public invoiceNumberExists;

    // ═══════════════════════════════════════════════════════════════════════
    // EVENTS
    // ═══════════════════════════════════════════════════════════════════════

    event InvoiceRegistered(
        string indexed invoiceId,
        string invoiceNumber,
        address indexed seller,
        address indexed buyer,
        uint256 amount
    );
    event InvoiceUpdated(string indexed invoiceId, InvoiceStatus newStatus);
    event PaymentRecorded(string indexed invoiceId, address payer, uint256 amount);
    event IPFSReferenceAdded(string indexed invoiceId, string ipfsCid);
    event ArweaveReferenceAdded(string indexed invoiceId, string arweaveTxId);
    event EscrowLinked(string indexed invoiceId, address escrowAddress);
    event InvoiceVerified(string indexed invoiceId, address verifier);

    // ═══════════════════════════════════════════════════════════════════════
    // CONSTRUCTOR
    // ═══════════════════════════════════════════════════════════════════════

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(REGISTRAR_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }

    // ═══════════════════════════════════════════════════════════════════════
    // REGISTRATION FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Register new invoice
    function registerInvoice(
        string calldata invoiceId,
        string calldata invoiceNumber,
        address seller,
        address buyer,
        uint256 totalAmount,
        uint256 issueDate,
        uint256 dueDate,
        string calldata ipfsCid,
        string calldata arweaveTxId
    ) external onlyRole(REGISTRAR_ROLE) nonReentrant {
        require(bytes(invoiceId).length > 0, "Invalid invoice ID");
        require(!invoices[invoiceId].exists, "Invoice already exists");
        require(seller != address(0), "Invalid seller");
        require(buyer != address(0), "Invalid buyer");
        require(totalAmount > 0, "Amount must be > 0");
        require(!invoiceNumberExists[invoiceNumber], "Invoice number exists");

        invoices[invoiceId] = InvoiceMetadata({
            invoiceId: invoiceId,
            invoiceNumber: invoiceNumber,
            seller: seller,
            buyer: buyer,
            totalAmount: totalAmount,
            paidAmount: 0,
            issueDate: issueDate,
            dueDate: dueDate,
            status: InvoiceStatus.Draft,
            ipfsCid: ipfsCid,
            arweaveTxId: arweaveTxId,
            escrowAddress: address(0),
            isRecurring: false,
            isFactored: false,
            exists: true
        });

        allInvoiceIds.push(invoiceId);
        sellerInvoices[seller].push(invoiceId);
        buyerInvoices[buyer].push(invoiceId);
        invoiceNumberExists[invoiceNumber] = true;

        emit InvoiceRegistered(invoiceId, invoiceNumber, seller, buyer, totalAmount);

        if (bytes(ipfsCid).length > 0) {
            emit IPFSReferenceAdded(invoiceId, ipfsCid);
        }
        if (bytes(arweaveTxId).length > 0) {
            emit ArweaveReferenceAdded(invoiceId, arweaveTxId);
        }
    }

    /// @notice Update invoice status
    function updateInvoiceStatus(
        string calldata invoiceId,
        InvoiceStatus newStatus
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        invoices[invoiceId].status = newStatus;
        emit InvoiceUpdated(invoiceId, newStatus);
    }

    /// @notice Record payment
    function recordPayment(
        string calldata invoiceId,
        address payer,
        uint256 amount,
        string calldata txHash,
        uint256 chainId
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        require(amount > 0, "Amount must be > 0");

        InvoiceMetadata storage invoice = invoices[invoiceId];
        invoice.paidAmount += amount;

        invoicePayments[invoiceId].push(InvoicePayment({
            payer: payer,
            amount: amount,
            timestamp: block.timestamp,
            txHash: txHash,
            chainId: chainId
        }));

        // Update status based on payment
        if (invoice.paidAmount >= invoice.totalAmount) {
            invoice.status = InvoiceStatus.Paid;
        } else if (invoice.paidAmount > 0) {
            invoice.status = InvoiceStatus.PartiallyPaid;
        }

        emit PaymentRecorded(invoiceId, payer, amount);
        emit InvoiceUpdated(invoiceId, invoice.status);
    }

    /// @notice Add IPFS reference
    function addIPFSReference(
        string calldata invoiceId,
        string calldata ipfsCid
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        invoices[invoiceId].ipfsCid = ipfsCid;
        emit IPFSReferenceAdded(invoiceId, ipfsCid);
    }

    /// @notice Add Arweave reference
    function addArweaveReference(
        string calldata invoiceId,
        string calldata arweaveTxId
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        invoices[invoiceId].arweaveTxId = arweaveTxId;
        emit ArweaveReferenceAdded(invoiceId, arweaveTxId);
    }

    /// @notice Link escrow contract
    function linkEscrow(
        string calldata invoiceId,
        address escrowAddress
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        require(escrowAddress != address(0), "Invalid escrow address");
        invoices[invoiceId].escrowAddress = escrowAddress;
        emit EscrowLinked(invoiceId, escrowAddress);
    }

    /// @notice Mark invoice as recurring
    function markAsRecurring(
        string calldata invoiceId,
        bool isRecurring
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        invoices[invoiceId].isRecurring = isRecurring;
    }

    /// @notice Mark invoice as factored
    function markAsFactored(
        string calldata invoiceId,
        bool isFactored
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        invoices[invoiceId].isFactored = isFactored;
    }

    // ═══════════════════════════════════════════════════════════════════════
    // VERIFICATION
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Verify invoice (for trusted verifiers)
    function verifyInvoice(
        string calldata invoiceId
    ) external onlyRole(VERIFIER_ROLE) {
        require(invoices[invoiceId].exists, "Invoice not found");
        emit InvoiceVerified(invoiceId, msg.sender);
    }

    // ═══════════════════════════════════════════════════════════════════════
    // BATCH OPERATIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Batch update invoice statuses
    function batchUpdateStatuses(
        string[] calldata invoiceIds,
        InvoiceStatus[] calldata statuses
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoiceIds.length == statuses.length, "Length mismatch");

        for (uint256 i = 0; i < invoiceIds.length; i++) {
            if (invoices[invoiceIds[i]].exists) {
                invoices[invoiceIds[i]].status = statuses[i];
                emit InvoiceUpdated(invoiceIds[i], statuses[i]);
            }
        }
    }

    /// @notice Batch record payments
    function batchRecordPayments(
        string[] calldata invoiceIds,
        address[] calldata payers,
        uint256[] calldata amounts,
        string[] calldata txHashes,
        uint256[] calldata chainIds
    ) external onlyRole(REGISTRAR_ROLE) {
        require(invoiceIds.length == amounts.length, "Length mismatch");
        require(invoiceIds.length == payers.length, "Length mismatch");

        for (uint256 i = 0; i < invoiceIds.length; i++) {
            if (invoices[invoiceIds[i]].exists && amounts[i] > 0) {
                this.recordPayment(
                    invoiceIds[i],
                    payers[i],
                    amounts[i],
                    txHashes[i],
                    chainIds[i]
                );
            }
        }
    }

    // ═══════════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════════

    /// @notice Get invoice metadata
    function getInvoice(string calldata invoiceId) external view returns (InvoiceMetadata memory) {
        require(invoices[invoiceId].exists, "Invoice not found");
        return invoices[invoiceId];
    }

    /// @notice Get invoice payments
    function getInvoicePayments(string calldata invoiceId) external view returns (InvoicePayment[] memory) {
        return invoicePayments[invoiceId];
    }

    /// @notice Get payment count
    function getPaymentCount(string calldata invoiceId) external view returns (uint256) {
        return invoicePayments[invoiceId].length;
    }

    /// @notice Get seller invoices
    function getSellerInvoices(address seller) external view returns (string[] memory) {
        return sellerInvoices[seller];
    }

    /// @notice Get buyer invoices
    function getBuyerInvoices(address buyer) external view returns (string[] memory) {
        return buyerInvoices[buyer];
    }

    /// @notice Get total invoice count
    function getTotalInvoices() external view returns (uint256) {
        return allInvoiceIds.length;
    }

    /// @notice Check if invoice exists
    function invoiceExists(string calldata invoiceId) external view returns (bool) {
        return invoices[invoiceId].exists;
    }

    /// @notice Get invoice by number
    function getInvoiceByNumber(string calldata invoiceNumber) external view returns (InvoiceMetadata memory) {
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            InvoiceMetadata memory invoice = invoices[allInvoiceIds[i]];
            if (keccak256(bytes(invoice.invoiceNumber)) == keccak256(bytes(invoiceNumber))) {
                return invoice;
            }
        }
        revert("Invoice not found");
    }

    /// @notice Get invoices by status
    function getInvoicesByStatus(InvoiceStatus status) external view returns (string[] memory) {
        uint256 count = 0;
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            if (invoices[allInvoiceIds[i]].status == status) {
                count++;
            }
        }

        string[] memory result = new string[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            if (invoices[allInvoiceIds[i]].status == status) {
                result[index] = allInvoiceIds[i];
                index++;
            }
        }

        return result;
    }

    /// @notice Get overdue invoices
    function getOverdueInvoices() external view returns (string[] memory) {
        uint256 count = 0;
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            InvoiceMetadata memory invoice = invoices[allInvoiceIds[i]];
            if (block.timestamp > invoice.dueDate && invoice.status != InvoiceStatus.Paid) {
                count++;
            }
        }

        string[] memory result = new string[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            InvoiceMetadata memory invoice = invoices[allInvoiceIds[i]];
            if (block.timestamp > invoice.dueDate && invoice.status != InvoiceStatus.Paid) {
                result[index] = allInvoiceIds[i];
                index++;
            }
        }

        return result;
    }

    /// @notice Get platform statistics
    function getStatistics() external view returns (
        uint256 totalInvoices,
        uint256 totalVolume,
        uint256 totalPaid,
        uint256 pendingCount,
        uint256 overdueCount,
        uint256 disputedCount
    ) {
        totalInvoices = allInvoiceIds.length;
        uint256 volume = 0;
        uint256 paid = 0;
        uint256 pending = 0;
        uint256 overdue = 0;
        uint256 disputed = 0;

        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            InvoiceMetadata memory invoice = invoices[allInvoiceIds[i]];
            volume += invoice.totalAmount;
            paid += invoice.paidAmount;

            if (invoice.status == InvoiceStatus.Pending || invoice.status == InvoiceStatus.PartiallyPaid) {
                pending++;
            }
            if (invoice.status == InvoiceStatus.Disputed) {
                disputed++;
            }
            if (block.timestamp > invoice.dueDate && invoice.status != InvoiceStatus.Paid) {
                overdue++;
            }
        }

        return (totalInvoices, volume, paid, pending, overdue, disputed);
    }
}

contracts/registry/UniversalRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title UniversalRegistry
 * @dev Universal name registry for any blockchain
 *
 * This contract provides a simple, gas-efficient name registration system
 * that can be deployed on any EVM-compatible chain to enable name services.
 *
 * Features:
 * - Domain registration with expiration
 * - Resolver management
 * - Owner management
 * - Grace period support
 * - Transfer/renewal functionality
 *
 * Compatible with ENS architecture for easy integration.
 */
contract UniversalRegistry {

    // ══════════════════════════════════════════════════════════════════════
    // STRUCTS
    // ══════════════════════════════════════════════════════════════════════

    struct Record {
        address owner;
        address resolver;
        uint64 expiry;
    }

    // ══════════════════════════════════════════════════════════════════════
    // STATE VARIABLES
    // ══════════════════════════════════════════════════════════════════════

    /// Top-level domain (e.g., "xdc", "hedera")
    string public tld;

    /// Registry owner (can add controllers)
    address public registryOwner;

    /// Namehash of the TLD (e.g., namehash("xdc"))
    bytes32 public tldNode;

    /// Grace period after expiration (default: 90 days)
    uint256 public constant GRACE_PERIOD = 90 days;

    /// Minimum registration duration (default: 28 days)
    uint256 public constant MIN_REGISTRATION_DURATION = 28 days;

    /// Records: namehash → Record
    mapping(bytes32 => Record) public records;

    /// Controllers: address → bool (can register names)
    mapping(address => bool) public controllers;

    // ══════════════════════════════════════════════════════════════════════
    // EVENTS
    // ══════════════════════════════════════════════════════════════════════

    event NameRegistered(
        bytes32 indexed node,
        string name,
        address indexed owner,
        uint256 expires
    );

    event NameRenewed(
        bytes32 indexed node,
        uint256 expires
    );

    event Transfer(
        bytes32 indexed node,
        address indexed newOwner
    );

    event NewResolver(
        bytes32 indexed node,
        address resolver
    );

    event ControllerAdded(address indexed controller);
    event ControllerRemoved(address indexed controller);

    // ══════════════════════════════════════════════════════════════════════
    // MODIFIERS
    // ══════════════════════════════════════════════════════════════════════

    modifier onlyOwner() {
        require(msg.sender == registryOwner, "Not registry owner");
        _;
    }

    modifier onlyController() {
        require(controllers[msg.sender], "Not a controller");
        _;
    }

    modifier onlyNodeOwner(bytes32 node) {
        require(records[node].owner == msg.sender, "Not domain owner");
        _;
    }

    modifier notExpired(bytes32 node) {
        require(records[node].expiry > block.timestamp, "Domain expired");
        _;
    }

    // ══════════════════════════════════════════════════════════════════════
    // CONSTRUCTOR
    // ══════════════════════════════════════════════════════════════════════

    constructor(string memory _tld, bytes32 _tldNode) {
        tld = _tld;
        tldNode = _tldNode;
        registryOwner = msg.sender;
        controllers[msg.sender] = true;
    }

    // ══════════════════════════════════════════════════════════════════════
    // REGISTRATION FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════

    /**
     * @dev Register a new name
     * @param node Namehash of the domain
     * @param name Human-readable name (for events)
     * @param owner Owner address
     * @param duration Registration duration in seconds
     */
    function register(
        bytes32 node,
        string calldata name,
        address owner,
        uint256 duration
    ) external onlyController returns (uint256) {
        require(duration >= MIN_REGISTRATION_DURATION, "Duration too short");
        require(owner != address(0), "Invalid owner");

        uint256 expiry = block.timestamp + duration;

        // Check if available (not registered or expired with grace period)
        Record storage record = records[node];
        if (record.owner != address(0)) {
            require(
                record.expiry + GRACE_PERIOD < block.timestamp,
                "Name not available"
            );
        }

        // Register
        record.owner = owner;
        record.expiry = uint64(expiry);

        emit NameRegistered(node, name, owner, expiry);

        return expiry;
    }

    /**
     * @dev Renew a name registration
     * @param node Namehash of the domain
     * @param duration Additional duration in seconds
     */
    function renew(bytes32 node, uint256 duration)
        external
        onlyController
        returns (uint256)
    {
        require(duration >= MIN_REGISTRATION_DURATION, "Duration too short");

        Record storage record = records[node];
        require(record.owner != address(0), "Name not registered");

        // Extend from current expiry or now (whichever is later)
        uint256 baseTime = record.expiry > block.timestamp
            ? record.expiry
            : block.timestamp;

        uint256 newExpiry = baseTime + duration;
        record.expiry = uint64(newExpiry);

        emit NameRenewed(node, newExpiry);

        return newExpiry;
    }

    // ══════════════════════════════════════════════════════════════════════
    // OWNER FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════

    /**
     * @dev Transfer domain ownership
     * @param node Namehash of the domain
     * @param newOwner New owner address
     */
    function transfer(bytes32 node, address newOwner)
        external
        onlyNodeOwner(node)
        notExpired(node)
    {
        require(newOwner != address(0), "Invalid new owner");

        records[node].owner = newOwner;

        emit Transfer(node, newOwner);
    }

    /**
     * @dev Set resolver for a domain
     * @param node Namehash of the domain
     * @param resolver Resolver contract address
     */
    function setResolver(bytes32 node, address resolver)
        external
        onlyNodeOwner(node)
        notExpired(node)
    {
        records[node].resolver = resolver;

        emit NewResolver(node, resolver);
    }

    // ══════════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════

    /**
     * @dev Get domain owner
     * @param node Namehash of the domain
     */
    function owner(bytes32 node) external view returns (address) {
        if (records[node].expiry < block.timestamp) {
            return address(0);
        }
        return records[node].owner;
    }

    /**
     * @dev Get resolver for a domain
     * @param node Namehash of the domain
     */
    function resolver(bytes32 node) external view returns (address) {
        if (records[node].expiry < block.timestamp) {
            return address(0);
        }
        return records[node].resolver;
    }

    /**
     * @dev Check if name is available for registration
     * @param node Namehash of the domain
     */
    function available(bytes32 node) external view returns (bool) {
        Record storage record = records[node];

        if (record.owner == address(0)) {
            return true;
        }

        // Available if expired + grace period passed
        return record.expiry + GRACE_PERIOD < block.timestamp;
    }

    /**
     * @dev Get expiry timestamp for a domain
     * @param node Namehash of the domain
     */
    function nameExpires(bytes32 node) external view returns (uint256) {
        return records[node].expiry;
    }

    // ══════════════════════════════════════════════════════════════════════
    // ADMIN FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════

    /**
     * @dev Add a controller (can register names)
     * @param controller Controller address
     */
    function addController(address controller) external onlyOwner {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    /**
     * @dev Remove a controller
     * @param controller Controller address
     */
    function removeController(address controller) external onlyOwner {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }

    /**
     * @dev Transfer registry ownership
     * @param newOwner New registry owner
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner");
        registryOwner = newOwner;
    }
}

